import React from "react";
import { data } from "../../helper/data";
import "./CardContainer.scss";

class CardContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCard: null,
    };
  }

  handleCardClick = (index) => {
    this.setState((prevState) => ({
      selectedCard: prevState.selectedCard === index ? null : index,
    }));
  };

  render() {
    const { selectedCard } = this.state;

    return (
      <div className="container">
        <div className="search">
          <input type="text" name="" id="" placeholder="Search Player..." />
        </div>
        <div className="cards">
          {data.map((player, index) => (
            <div key={index} onClick={() => this.handleCardClick(index)}>
              <div className={`card ${selectedCard === index ? "hide" : ""}`}>
                <img src={player.img} alt="player-img" />
                <h3>{player.name}</h3>
              </div>
              {selectedCard === index && (
                <div className="stats">
                  {player.statistics.map((statistic, statIndex) => (
                    <li key={statIndex}>{statistic}</li>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default CardContainer;
